import React, { useState } from "react";
import '@fortawesome/fontawesome-svg-core/styles.css'

function Audio() {
    const [audioFile, setAudioFile] = useState(null);
    const [responseText, setResponseText] = useState('HIII');
    const [isExpanded, setExpanded] = useState(false)

    const handleFileChange = (event) => {
        setAudioFile(event.target.files[0]);
        if (!audioFile) {
            alert("Please select a file first.");
            return;
        }
    };

    const toggleExpansion = () => {
        setExpanded(!isExpanded)
    }

    const generateText = async () => {
        if (!audioFile) {
            alert("Please select a file first.");
            return;
        }

        const formData = new FormData();
        formData.append("file", audioFile);

        try {
            const response = await fetch('/audio-generated-text', {
                method: 'POST',
                body: formData,
            });

            if (response.ok) {
                const result = await response.text();
                setResponseText(result);
                alert('Text generated successfully');
            } else {
                alert('Failed to generate text');
            }
        } catch (error) {
            console.error('Error:', error);
            alert('Error generating text');
        }
    };

    return (
        <div className="file-container">
            <h2>Add Audio File</h2>
            <div className="file-group">
                <input
                    type="file"
                    accept="audio/mp3, audio/mpg, audio/ogg"
                    onChange={handleFileChange}
                />
                <button onClick={toggleExpansion} style={{width: '15rem'}}>
                    {isExpanded ?
                        <i className="fa fa-arrow-down" aria-hidden="true"></i>
                        : <i className="fa fa-arrow-up" aria-hidden="true"></i>
                    } View Response
                    {responseText && <p>Generated Text: {responseText}</p>}
                </button>
                {responseText && <p>Generated Text: {responseText}</p>}
            </div>

            <br></br>
            <divider style={{border: 'solid gray 1px'}}></divider>
        </div>
    );
}

export default Audio;
