import React, { useState } from "react";
import '@fortawesome/fontawesome-svg-core/styles.css';
import axios from "axios";

function Audio() {
    const [audioFile, setAudioFile] = useState(null);
    const [responseText, setResponseText] = useState("");
    const [isExpanded, setExpanded] = useState(false)
    const [trascription, setTranscription] = useState('')

    const handleFileChange = (event) => {
        setAudioFile(event.target.files[0]);
    };

    const handleUpload = async () => {
        setExpanded(!isExpanded)

        const formData = new FormData();
        formData.append("file", audioFile);

        try{
            const response = await axios.post('api/transcribe', formData, {
                headers: {
                    'Content-Type':'multipart/form-data',
                }
            });
            setTranscription(response.data)
        } catch (error) {
            console.error("Error transcribing ", error)
        }
        // try {
        //     const response = await fetch('/audio-generated-text', {
        //         method: 'POST',
        //         body: formData,
        //     });
        //
        //     if (response.ok) {
        //         const result = await response.text();
        //         setResponseText(result);
        //         alert('Text generated successfully');
        //     } else {
        //         alert('Failed to generate text');
        //     }
        // } catch (error) {
        //     console.error('Error:', error);
        //     alert('Error generating text');
        // }
    };

    return (
        <div className="file-container">
            <h2>Add Audio File</h2>
            <div className="file-group">
                <input
                    type="file"
                    accept="audio/*"
                    onChange={handleFileChange}
                />
                <button onClick={handleUpload} style={{width: '15rem'}}>
                    {isExpanded ?
                        <i className="fa fa-arrow-down" aria-hidden="true"></i>
                        : <i className="fa fa-arrow-up" aria-hidden="true"></i>
                    } View Response
                </button>
            </div>
            {trascription && isExpanded && <p>Generated Text: {trascription}</p>}
            <br></br>
            <divider style={{border: 'solid gray 1px'}}></divider>
        </div>
    );
}

export default Audio;
