import React, { useState } from "react";

function Image() {
    const [imageFile, setImageFile] = useState(null);
    const [responseText, setResponseText] = useState('');

    const handleFileChange = (event) => {
        setImageFile(event.target.files[0]);
    };

    const tesseractAI = async () => {
        if (!imageFile) {
            alert("Please select a file first.");
            return;
        }

        const formData = new FormData();
        formData.append("file", imageFile);

        try {
            const response = await fetch('/image-generated-text', {
                method: 'POST',
                body: formData,
            });

            if (response.ok) {
                const result = await response.text();
                setResponseText(result);
                alert('Text generated successfully');
            } else {
                alert('Failed to generate text');
            }
        } catch (error) {
            console.error('Error:', error);
            alert('Error generating text');
        }
    };
//only certain file types
    return (
        <div className="FileContainer">
            <h2>Add Image File</h2>
            <input
                type="file"
                onChange={handleFileChange}
            />
            <FontAwesomeIcon icon="fa-solid fa-chevron-up" />
            <FontAwesomeIcon icon="fa-solid fa-chevron-down" />
            <div>
              {responseText && <p>Generated Text: {responseText}</p>}
            </div>
        </div>
    );
}

export default Image;
